mapper.select:
select * from record r
    join question q on r.record_question_id = q.question_id
    where r.record_sid = #{sid} and q.question_of_assignment = #{assignment_id}

mapper.update:
update record
 set record_status = #{record_status}
     where record_id = #{record_id};

